select PGN_SHARE.tbl_user.id_user AS id_user,PGN_SHARE.tbl_user.username AS username,PGN_SHARE.tbl_user.email AS email,PGN_SHARE.tbl_user.nama AS nama,PGN_SHARE.tbl_user.jabatan AS jabatan,PGN_SHARE.tbl_user.password AS password,PGN_SHARE.tbl_user.role AS role,PGN_SHARE.tbl_role.role_name AS role_name,PGN_SHARE.tbl_user.area_kerja AS area_kerja,PGN_SHARE.tbl_user.status AS status,PGN_SHARE.tbl_user.foto AS foto,PGN_SHARE.tbl_user.remember_token AS remember_token,PGN_SHARE.tbl_user.created_at AS created_at,PGN_SHARE.tbl_user.updated_at AS updated_at from (PGN_SHARE.tbl_user join PGN_SHARE.tbl_role on((PGN_SHARE.tbl_user.role = PGN_SHARE.tbl_role.role)))